		showriqi(res , pathname);
	}else if(/\/pinyin\/.+$/.test(pathname)){
		showpinyin(res , pathname);
	}else{
		res.statusCode = 404;
		res.setHeader("Content-Type" , "text/html;charset=UTF8");
